  'use strict';
  exports.platforms = ['browser'];
  exports.after = ['render'];
  exports.synchronous = true;
  exports.startup = () => {
    require('$:/plugins/oeyoews/motion/modules/library/mousetrap.min.js');
    const Motion = require('$:/plugins/oeyoews/motion/modules/library/Motion.min.js');
    new Motion().init();
  };
})();
