"},"$:/config/ShortcutInfo/notebook-focus-search":{"title":"$:/config/ShortcutInfo/notebook-focus-search","text":"Focus on the topbar search field"},"$:/config/shortcuts/notebook-focus-search":{"title":"$:/config/shortcuts/notebook-focus-search","text":"ctrl+shift+F"},"$:/config/Search/AutoFocus":{"title":"$:/config/Search/AutoFocus","text":"false"},"$:/config/shortcuts/sidebar-search":{"title":"$:/config/shortcuts/sidebar-search","text":""},"$:/themes/nico/notebook/custom":{"title":"$:/themes/nico/notebook/custom","text":"/* disable scrollbar */
* {
  scrollbar-width: none;
  -ms-overflow-style: none;
}
::-webkit-scrollbar,
scrollbar-width {
  display: none;
}
::-webkit-scrollbar:hover,
scrollbar-width {
  display: none;
}
::-moz-scrollbar {
  display: none;
}

/* gradient hr */
hr {
  height: 2px;
  background-image: linear-gradient(135deg, #12c2e9, #c9ffbf);
  border: none !important;
  border-radius: 3px;
  margin: 0.25rem 0 !important;
}

.gradient {
  /* display: inline; */
  background-image: linear-gradient(135deg, #12c2e9, #ffafbd, #c9ffbf);
  -webkit-background-clip: text;
  background-clip: text;
  color: transparent;
}

/* better focus-visible */
/* disable it */
:focus-visible {
  /* outline: #7f4bca 2px solid !important; */
  outline: none;
}

blockquote {
  border-left: 3px solid #dfdfdf;
}

blockquote > div {
  margin: 4px 1px;
}

/* remove story border */
.tc-tiddler-frame {
  border: 0;
}

/* remove editor border */
.tc-editor-toolbar button,
.tc-editor-toolbar button.tc-btn-invisible {
  border: none !important;
}

.tc-edit-tags {
  border: none;
}

.tc-tiddler-frame input.tc-edit-texteditor {
  border: none;
}

/* remove extra underline with image lin */
a {
  color: #007bff;
  text-decoration: none !important;
}

/* remove more border */
.tc-tab-content.tc-vertical {
  border-left: 0;
}

.tc-more-sidebar > .tc-tab-set > .tc-tab-buttons > button.tc-tab-selected {
  border: none;
}

.tc-more-sidebar > .tc-tab-set > .tc-tab-buttons > button {
  border: none;
}

/* tc-notification */
.tc-notification {
  color: black;
  border-radius: 5px;
  /** background: linear-gradient(45deg, #12c2e9, #c471ed, #f64f59); */
  border: 2px;
  box-shadow: 0 5px 20px rgb(0 0 0 / 12%);
}

/* better editor bar @kookma plugin */
.tc-tiddler-controls {
  & .tc-image-delete-button {
    fill: #ebb;
  }
  & .tc-image-cancel-button {
    fill: #ed9;
  }
  & .tc-image-done-button {
    fill: #beb;
  }
}

/* just show btn on hover */
.tc-tiddler-frame .tc-titlebar button {
  opacity: 0;
  transition: opacity 0.5s ease-in-out;
}
.tc-tiddler-frame:hover .tc-titlebar button {
  zoom: 1;
  filter: alpha(opacity=100);
  opacity: 1;
}
/* enhance font size */
.tc-tiddler-frame .tc-tiddler-body {
  font-size: 20px;
}

