/* remove btn border */
button {
  cursor: pointer;
  border: none;
  border-radius: 3px;
  background: #00000008;
  text-transform: capitalize;
}

.tc-modal-backdrop {
  cursor: pointer;
}

.tc-tab-divider,
.tc-plugin-info,
.tc-plugin-info-dropdown {
  border: none;
}

/* switch layout */
.tc-chosen,
.tc-chooser-item:hover {
  border: none;
}

select {
  border: none;
}

/* editor view */
.tc-tiddler-preview-preview {
  border: none !important;
  font-size: 20px;
}

.nc-bar input[type='search']:focus {
