.tc-tiddler-title {
  position: -webkit-sticky;
  position: -moz-sticky;
  position: -o-sticky;
  position: -ms-sticky;
  position: sticky;
  top: {{$:/themes/nico/notebook/metrics/topbar-height}};
  background: <<colour tiddler-background>>;
  z-index: 500;
}

@media (max-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {
  .tc-tiddler-title {
    top: 0;
  }
}

