"},"$:/themes/nico/notebook/ui/Buttons/menu":{"title":"$:/themes/nico/notebook/ui/Buttons/menu","text":"<!-- tags: $:/tags/PageControls -->
<!-- description: -->
<!-- list-after: -->
<!-- caption: -->

<$reveal state="$:/state/notebook-sidebar" type="match" text="yes" default="no" retain="yes" animate="no">
  <$button set="$:/state/notebook-sidebar" setTo="no" tooltip="Toggle menu" class="tc-btn-invisible sidebar-toggle open">
    {{$:/themes/nico/notebook/images/bars}}
  </$button>
</$reveal>

<$reveal type="nomatch" state="$:/state/notebook-sidebar" text="yes">
  <$button set="$:/state/notebook-sidebar" setTo="yes" tooltip="Toggle menu" class="tc-btn-invisible sidebar-toggle">
    {{$:/themes/nico/notebook/images/bars}}
  </$button>
</$reveal>
"},"$:/themes/nico/notebook/ui/Buttons/SwitchPalette":{"title":"$:/themes/nico/notebook/ui/Buttons/SwitchPalette","description":"Toggle between grey/beige colour palette","tags":"$:/tags/PageControls","type":"text/vnd.tiddlywiki","text":"<span class="desktop-only">
  <$vars
    palettes="[all[tiddlers+shadows]tag[$:/tags/notebook/Palette]]"
    popupTiddler=<<qualify "$:/state/notebook/palette-dropdown">>
  >
    <$button
      popup=<<popupTiddler>>
      tooltip="Switch colours"
      aria-label="Switch colours"
      class=<<tv-config-toolbar-class>>
    >
      <$list filter="[<tv-config-toolbar-icons>match[yes]]">
	{{$:/themes/nico/notebook/images/color-switch}}
      </$list>

      <$list filter="[<tv-config-toolbar-text>match[yes]]">
	<span class="tc-btn-text">Switch colours</span>
      </$list>

      <$reveal state=<<popupTiddler>> type="popup" position="belowleft" class="tc-drop-down">
	<$list filter=<<palettes>>>
	  <$button class="tc-btn-invisible">
	    {{!!name}}
	    <$action-setfield $tiddler="$:/palette" text={{!!title}}/>
	  </$button>
	</$list>
      </$reveal>

    </$button>
  </$vars>
</span>
"},"$:/themes/nico/notebook/ui/Search":{"title":"$:/themes/nico/notebook/ui/Search","type":"text/vnd.tiddlywiki","text":"\define advanced-search-actions()
<$action-setfield $tiddler="$:/temp/advancedsearch/input" text={{$:/temp/notebook-search}}/>
<$action-setfield $tiddler="$:/temp/advancedsearch/refresh" text="yes"/>
<$action-navigate $to="$:/AdvancedSearch"/>
\end

\define input-accept-actions()
<$list filter="[<__tiddler__>get[text]!is[missing]] ~[<__tiddler__>get[text]is[shadow]]">
  <$action-navigate $to={{{ [<__tiddler__>get[text]] }}}/>
  <$action-deletetiddler $filter="[[$:/temp/search]] [<searchTiddler>] [<searchListState>]"/>
</$list>
\end

\define cancel-search-actions()
<$list filter="[<searchTiddler>get[text]!match{$:/temp/search}]" emptyMessage="""<$action-deletetiddler $filter="[[$:/temp/search]] [<searchTiddler>] [<searchListState>]"/>""">
  <$action-setfield $tiddler="$:/temp/search" text={{{ [<searchTiddler>get[text]] }}}/>
  <$action-setfield $tiddler="$:/temp/search/refresh" text="yes"/></$list>
\end

<$vars editTiddler="$:/temp/search"
       searchTiddler="$:/temp/search/input"
       searchListState=<<qualify "$:/state/search-list/selected-item">>>
  <$macrocall $name="keyboard-driven-input"
	      tiddler=<<editTiddler>>
	      storeTitle=<<searchTiddler>>
	      selectionStateTitle=<<searchListState>>
	      refreshTitle="$:/temp/search/refresh"
	      type="search"
	      tag="input"
	      focus={{$:/config/Search/AutoFocus}}
	      focusPopup="$:/state/popup/notebook-search"
	      class="tc-popup-handle"
	      filterMinLength={{$:/config/Search/MinLength}}
	      placeholder="Search..."
	      inputAcceptActions=<<input-accept-actions>>
	      inputCancelActions=<<cancel-search-actions>>
	      cancelPopups="yes"
	      configTiddlerFilter="[[$:/state/search/currentTab]!is[missing]get[text]] ~[{$:/config/SearchResults/Default}]"
	      />
  <$button
    tooltip={{$:/language/Buttons/AdvancedSearch/Hint}}
    aria-label={{$:/language/Buttons/AdvancedSearch/Caption}}
    class="tc-btn-invisible tc-page-controls"
    >
    {{$:/core/images/advanced-search-button}}
    <<advanced-search-actions>>
  </$button>
  <$reveal tag="div" class="tc-block-dropdown-wrapper" state="$:/state/popup/notebook-search" type="nomatch" text="" default="">
    <$list filter="[<searchTiddler>get[text]minlength{$:/config/Search/MinLength}limit[1]]" emptyMessage="" variable="listItem">
      <div class="tc-block-dropdown tc-search-drop-down">
        <$tiddler tiddler=<<configTiddler>>>
          {{$:/themes/nico/notebook/ui/Sidebar/SearchResults}}
        </$tiddler>
      </div>
    </$list>
  </$reveal>
</$vars>
"},"$:/themes/nico/notebook/ui/Sidebar/Headings":{"title":"$:/themes/nico/notebook/ui/Sidebar/Headings","tags":"$:/themes/nico/notebook/tags/Sidebar","type":"text/vnd.tiddlywiki","text":"<div class="segment">
  <div class="content">
    <h1 class="tc-site-title">
      {{$:/SiteTitle}}
    </h1>
    <div class="tc-site-subtitle">
      {{$:/SiteSubtitle}}
    </div>
  </div>
</div>
"},"$:/themes/nico/notebook/ui/Sidebar/Search":{"title":"$:/themes/nico/notebook/ui/Sidebar/Search","tags":"$:/themes/nico/notebook/tags/Sidebar","type":"text/vnd.tiddlywiki","text":"<div class="mobile-only">
  <div class="segment">
    <div class="content search">
      {{$:/themes/nico/notebook/ui/Search}}
    </div>
  </div>
</div>
"},"$:/themes/nico/notebook/ui/Sidebar/SearchResults":{"title":"$:/themes/nico/notebook/ui/Sidebar/SearchResults","type":"text/vnd.tiddlywiki","text":"\define searchResultList()
  <small>{{$:/language/Search/Matches/Title}}</small>

  <$list filter="[!is[system]search:title{$(searchTiddler)$}sort[title]limit[250]]">
    <span class={{{[<currentTiddler>addsuffix[-primaryList]] -[<searchListState>get[text]] +[then[]else[tc-list-item-selected]] }}}>
      <$transclude tiddler="$:/core/ui/ListItemTemplate"/>
    </span>
  </$list>

  <small>{{$:/language/Search/Matches/All}}</small>

  <$list filter="[!is[system]search{$(searchTiddler)$}sort[title]limit[250]]">
    <span class={{{[<currentTiddler>addsuffix[-secondaryList]] -[<searchListState>get[text]] +[then[]else[tc-list-item-selected]] }}}>
      <$transclude tiddler="$:/core/ui/ListItemTemplate"/>
    </span>
  </$list>
\end

<div class="tc-search-results">
  <<searchResultList>>
</div>
"},"$:/themes/nico/notebook/ui/Sidebar/Sections":{"title":"$:/themes/nico/notebook/ui/Sidebar/Sections","tags":"$:/themes/nico/notebook/tags/Sidebar","type":"text/vnd.tiddlywiki","text":"<$list filter="[all[shadows+tiddlers]!has[draft.of]tag[$:/tags/SideBar]]">
  {{||$:/themes/nico/notebook/ui/Sidebar/SectionTemplate}}
</$list>
"},"$:/themes/nico/notebook/ui/Sidebar/SectionTemplate":{"title":"$:/themes/nico/notebook/ui/Sidebar/SectionTemplate","type":"text/vnd.tiddlywiki","text":"\define sidebarHeading()
<$vars tv-wikilinks="no">
  <$transclude field="caption">
    <$view field="title"/>
  </$transclude>
</$vars>
\end

<$reveal state="$:/state/notebook-sidebar-section" type="match" text=<<currentTiddler>> default="no" animate="no">
  <div class="segment section open">
    <$button set="$:/state/notebook-sidebar-section" setTo="" class="tc-btn-invisible label">
      <<sidebarHeading>>
      <span class="caret">{{$:/themes/nico/notebook/images/caret-down}}</span>
    </$button>
    <div class="content">
      <$transclude $tiddler=<<currentTiddler>> mode="block"/>
    </div>
  </div>
</$reveal>
<$reveal state="$:/state/notebook-sidebar-section" type="nomatch" text=<<currentTiddler>> default="yes" animate="no">
  <div class="segment section">
    <$button set="$:/state/notebook-sidebar-section" setTo=<<currentTiddler>> class="tc-btn-invisible label">
      <<sidebarHeading>>
      <span class="caret">{{$:/themes/nico/notebook/images/caret-right}}</span>
    </$button>
  </div>
</$reveal>
"},"$:/themes/nico/notebook/ui/Sidebar":{"title":"$:/themes/nico/notebook/ui/Sidebar","tags":"$:/tags/PageTemplate","type":"text/vnd.tiddlywiki","text":"\whitespace trim
\define config-title()
$:/config/SideBarSegments/Visibility/$(listItem)$
\end

<$reveal state="$:/state/notebook-sidebar" type="match" text="yes" default="no" retain="yes" animate="no">
    <$scrollable fallthrough="no">
    <div class="nc-sidebar p-2 z-[900] hidden lg:block">
      <$list filter="[all[shadows+tiddlers]tag[$:/themes/nico/notebook/tags/Sidebar]!has[draft.of]]" variable="listItem">
        <$reveal type="nomatch" state=<<config-title>> text="hide" tag="div">
          <$transclude tiddler=<<listItem>> mode="inline"/>
        </$reveal>
      </$list>
    </div>
    </$scrollable>
</$reveal>
"},"$:/themes/nico/notebook/ui/toggle-sidebar-positon":{"title":"$:/themes/nico/notebook/ui/toggle-sidebar-positon","tags":"$:/tags/PageControls","list-before":"$:/core/ui/Buttons/more-page-actions","description":"Position","caption":"Position","text":"\whitespace trim
<!-- this positon not refreshed -->
<!-- <$button tooltip={{{ [[$:/themes/nico/notebook/metrics/sidebar-position]get[position]addprefix[Toggle sidebar position ]] }}} aria-label="Position" class="tc-btn-invisible grayscale hover:filter-none"> -->
<$button tooltip={{{ [[Toggle sidebar position]] }}} aria-label="Position" class="tc-btn-invisible grayscale hover:filter-none">
<$list filter="[<tv-config-toolbar-icons>match[yes]]">
<$list filter="[[$:/themes/nico/notebook/metrics/sidebar-position]get[position]match[right]]">
  {{$:/themes/nico/notebook/images/leftbar.svg}}
</$list>
<$list filter="[[$:/themes/nico/notebook/metrics/sidebar-position]get[position]match[left]]">
  {{$:/themes/nico/notebook/images/rightbar.svg}}
</$list>
 <$action-listops $tiddler="$:/themes/nico/notebook/metrics/sidebar-position" $field="position" $subfilter="+[toggle[left],[right]]" />
  <$action-setfield $tiddler="$:/state/notebook-sidebar" text="yes"/>
</$list>
<$list filter="[<tv-config-toolbar-text>match[yes]]">
<span class="tc-btn-text">
<$text text="Toggle Sidebar Position"/>
</span>
</$list>
</$button>"},"$:/themes/nico/notebook/ui/Topbar":{"title":"$:/themes/nico/notebook/ui/Topbar","tags":"$:/tags/PageTemplate","text":"<!-- todo add option -->
<!-- add indicator like ios bottom bar -->
<$reveal state="$:/state/notebook-topbar" type="match" text="yes" default="yes" retain="yes" animate="yes">
  <!-- w-full  -->
  <div class="nc-topbar-wrapper hidden md:block transition-all w-56 h-2 rounded-md hover:rounded-none hover:w-full hover:h-1/6 hover:bg-transparent bg-neutral-200 dark:bg-black backdrop-blur fixed left-1/2 -translate-x-1/2 hover:translate-x-0 top-0 hover:left-0 cursor-pointer group p-1 duration-600 ease-in-out z-[999] overflow-auto">
  <!-- <div class="text-center group-hover:hidden text-sm"></div> -->
  <!-- https://github.com/Jermolene/TiddlyWiki5/blob/526e997aa4de03acdc2ff56ac7c19b3ff06cbb64/editions/tw5.com/tiddlers/hiddensettings/Hidden%20Setting_%20Scroll%20Top%20Adjustment.tid#L9 -->
    <div class="nc-bar nc-topbar tc-adjust-top-of-scroll mb-4 bg-transparent scale-0 group-hover:scale-100">
      <$list filter="[all[shadows+tiddlers]tag[$:/tags/NotebookTopbar]!has[draft.of]]" variable="listItem">
        <$reveal type="nomatch" state=<<config-title>> text="hide" tag="div">
          <$transclude tiddler=<<listItem>> mode="block"/>
        </$reveal>
      </$list>
      <div id="page-controls">
        {{$:/core/ui/SideBarSegments/page-controls}}
      </div>
      <div id="menu">
      	{{$:/themes/nico/notebook/ui/Buttons/menu}}
      </div>
    </div>
  </div>
</$reveal>
"}}}
